Tree.propsAlt:
export type TreeProps<Item extends TreeItem, Multiple extends boolean = false> = {
  /**
   * If `true`, the user can select multiple items.
   * @default false
   */
  multiple?: MaybeGetter<Multiple | undefined>;
  /**
   * The currently selected item(s).
   * If `multiple` is `true`, this should be an `Iterable`.
   * Otherwise, it'll be a `string`.
   * @default undefined
   */
  selected?: MaybeMultiple<string, Multiple>;
  /**
   * Callback fired when selection changes
   * @param value - For multiple selection, a Set of selected IDs. For single selection, a single ID or undefined
   */
  onSelectedChange?: (value: Multiple extends true ? Set<string> : string | undefined) => void;
  /**
   * The currently expanded items
   *
   * @default undefined
   */
  expanded?: MaybeMultiple<string, true>;
  /**
   * Callback fired when expanded state changes
   * @param value - Set of expanded item IDs
   */
  onExpandedChange?: (value: Set<string>) => void;
  /**
   * If `true`, groups (items with children) expand on click.
   * @default true
   */
  expandOnClick?: MaybeGetter<boolean | undefined>;
  /**
   * The items contained in the tree.
   * @required
   */
  items: IterableProp<Item>;
  /**
   * How many time (in ms) the typeahead string is held before it is cleared
   * @default 500
   */
  typeaheadTimeout?: MaybeGetter<number | undefined>;
};

Toggle.propsAlt:
export type ToggleProps = {
  /**
   * The value for the Toggle.
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that the value only changes when the getter returns a new value.
   *
   * Otherwise, if passing a static value, it'll serve as the default value.
   *
   *
   * @default false
   */
  value?: MaybeGetter<boolean>;
  /**
   * Called when the value is supposed to change.
   */
  onValueChange?: (value: boolean) => void;

  /**
   * If `true`, prevents the user from interacting with the input.
   *
   * @default false
   */
  disabled?: MaybeGetter<boolean | undefined>;
};

TagsInput.propsAlt:
export type TagsInputProps = {
  /**
   * The values for the tags.
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that the value only changes when the getter returns a new value.
   *
   * Otherwise, if passing a static value, it'll serve as the default values.
   */
  tags?: MaybeGetter<Tag[] | undefined>;

  /**
   * The placeholder text for the input element.
   */
  placeholder?: MaybeGetter<string | undefined>;

  /**
   * Whether or not the tags input is disabled.
   * @default false
   */
  disabled?: MaybeGetter<boolean | undefined>;

  /**
   * Whether or not the input is editable.
   * @default true
   */
  editable?: MaybeGetter<boolean | undefined>;

  /**
   * The selected tag.
   */
  selected?: MaybeGetter<Tag | undefined>;

  /**
   * Whether or not the tags input should only allow unique tags.
   * @default false
   */
  unique?: MaybeGetter<boolean | undefined>;

  /**
   * Whether or not whitespace from both ends of input string should be removed when a tag is added.
   * @default true
   */
  trim?: MaybeGetter<boolean | undefined>;

  /**
   * Define the action that should be taken when the input element loses focus (blurs).
   * 'nothing' - Left over strings in the input are left as they are.
   * 'add' - Left over strings get added as a tag.
   * 'clear' - Left over strings get cleared, so the input is empty again.
   * @default 'nothing'
   */
  blur?: MaybeGetter<Blur | undefined>;

  /**
   * Whether or not the input should add tags on paste.
   * @default false
   */
  addOnPaste?: MaybeGetter<boolean | undefined>;

  /**
   * The maximum number of tags allowed.
   */
  maxTags?: MaybeGetter<number | undefined>;

  /**
   * The allowed tags.
   */
  allowed?: MaybeGetter<string[] | undefined>;

  /**
   * The disallowed tags.
   */
  denied?: MaybeGetter<string[] | undefined>;

  /**
   * A function that is called when the tags change.
   */
  onTagsChange?: (value: Tag[]) => void;

  /**
   * Optional validator/parser function that runs on tag addition.
   *
   * If an error is thrown, or the promise is rejected, the tag will not be added.
   *
   * Otherwise, return a Tag or a string for the tag to be added.
   *
   * @param tag The tag to be added
   */
  add?: (tag: string) => (Tag | string | never) | Promise<Tag | string | never>;

  /**
   * Optional validator/parser function that runs on tag removal.
   *
   * If an error is thrown, the promise is rejected, and that will not be removed.
   * If `false` is returned, the tag will also not be removed.
   *
   * Otherwise, return `true` for the tag to be removed.
   *
   * @param tag The tag to be removed
   */
  remove?: (tag: Tag) => (boolean | never) | Promise<boolean | never>;
};

Tabs.propsAlt:
export type TabsProps<T extends string = string> = {
  /**
   * If `true`, the value will be changed whenever a trigger is focused.
   *
   * @default true
   */
  selectWhenFocused?: MaybeGetter<boolean | undefined>;
  /**
   * If the the trigger selection should loop when navigating with the arrow keys.
   *
   * @default true
   */
  loop?: MaybeGetter<boolean | undefined>;
  /**
   * The orientation of the tabs.
   *
   * @default "horizontal"
   */
  orientation?: MaybeGetter<"horizontal" | "vertical" | undefined>;
  /**
   * The default value for `tabs.value`
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that `tabs.value` only changes when the getter returns a new value.
   *
   * If omitted, it will use the first tab as default.
   *
   * @default undefined
   */
  value?: MaybeGetter<T | undefined>;
  /**
   * Called when the `Tabs` instance tries to change the active tab.
   */
  onValueChange?: (active: T) => void;
};

Slider.propsAlt:
export type SliderProps = {
  /**
   * The minimum value of the slider.
   *
   * @default 0
   */
  min?: MaybeGetter<number | undefined>;
  /**
   * The maximum value of the slider.
   *
   * @default 100
   */
  max?: MaybeGetter<number | undefined>;
  /**
   * The orientation of the slider.
   *
   * @default "horizontal"
   */
  orientation?: MaybeGetter<"horizontal" | "vertical" | undefined>;

  /**
   * The step size of the slider.
   *
   * @default 1
   */
  step?: MaybeGetter<number | undefined>;
  /**
   * The default value for `tabs.value`
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that `tabs.value` only changes when the getter returns a new value.
   *
   * If omitted, it will use the first tab as default.
   *
   * @default undefined
   */
  value?: MaybeGetter<number | undefined>;
  /**
   * Called when the `Slider` instance tries to change the active tab.
   */
  onValueChange?: (active: number) => void;
};

Select.propsAlt:
export type SelectProps<T extends string, Multiple extends boolean = false> = PopoverProps & {
  /**
   * If `true`, multiple options can be selected at the same time.
   *
   * @default false
   */
  multiple?: MaybeGetter<Multiple | undefined>;

  /**
   * The value for the Select.
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that the value only changes when the getter returns a new value.
   *
   * Otherwise, if passing a static value, it'll serve as the default value.
   *
   *
   * @default false
   */
  value?: MaybeMultiple<T, Multiple>;
  /**
   * Called when the value is supposed to change.
   */
  onValueChange?: OnMultipleChange<T, Multiple>;

  /**
   * How many time (in ms) the typeahead string is held before it is cleared
   * @default 500
   */
  typeaheadTimeout?: MaybeGetter<number | undefined>;
};

RadioGroup.propsAlt:
export type RadioGroupProps = {
  /**
   * If `true`, prevents the user from interacting with the group.
   *
   * @default false
   */
  disabled?: MaybeGetter<boolean | undefined>;
  /**
   * If `true`, indicates that the user must select a radio button before
   * the owning form can be submitted.
   *
   * @default false
   */
  required?: MaybeGetter<boolean | undefined>;
  /**
   * If the the button selection should loop when navigating with the arrow keys.
   *
   * @default true
   */
  loop?: MaybeGetter<boolean | undefined>;
  /**
   * If `true`, the value will be changed whenever a button is focused.
   *
   * @default true
   */
  selectWhenFocused?: MaybeGetter<boolean | undefined>;
  /**
   * The orientation of the slider.
   *
   * @default "vertical"
   */
  orientation?: MaybeGetter<"horizontal" | "vertical" | undefined>;
  /**
   * Input name for radio group.
   */
  name?: MaybeGetter<string | undefined>;
  /**
   * Default value for radio group.
   *
   * @default ""
   */
  value?: MaybeGetter<string | undefined>;
  /**
   * Called when the radio button is clicked.
   */
  onValueChange?: (active: string) => void;
};

Progress.propsAlt:
export type ProgressProps = {
  /**
   * The value for the progress.
   * 
   * @default undefined
   */
  value?: MaybeGetter<number | undefined>;

  /**
   * The maximum value of the progress.
   * 
   * @deafult 100
   */
  max?: MaybeGetter<number | undefined>;

  /**
   * The callback invoked when the value of the progress changes.
   */
  onValueChange?: (value: number) => void;
};

Popover.propsAlt:
export type PopoverProps = {
  /**
   * If the Popover is open.
   *
   * When passing a getter, it will be used as source of truth,
   * meaning that the value only changes when the getter returns a new value.
   *
   * Otherwise, if passing a static value, it'll serve as the default value.
   *
   *
   * @default false
   */
  open?: MaybeGetter<boolean | undefined>;

  /**
   * Called when the value is supposed to change.
   */
  onOpenChange?: (value: boolean) => void;

  /**
   * If the popover visibility should be controlled by the user.
   *
   * @default false
   */
  forceVisible?: MaybeGetter<boolean | undefined>;

  /**
   * Options to be passed to Floating UI's `computePosition`
   *
   * @see https://floating-ui.com/docs/computePosition
   */
  computePositionOptions?: MaybeGetter<Partial<ComputePositionConfig> | undefined>;

  /**
   * If the popover should have the same width as the trigger
   *
   * @default false
   */
  sameWidth?: MaybeGetter<boolean | undefined>;
};

